import React, { useState } from 'react';
import './FaqItem.css';
import classes from './About.module.css'
import girlImage from '../../assets/about-7.jpg'

const FaqItem = ({ question, answer }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleAnswer = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={classes.About}>
            <img src={girlImage} alt="workoutImage" style={{height: '650px', marginLeft: 0, width: '2000px'}} />
            <div>
                <h3>//Some FAQ's</h3>
                <h1>Frequently Asking</h1>
                <h1>Questions</h1>
            </div>
            <div className={`faq-item ${isOpen ? 'active' : ''}`} onClick={toggleAnswer}>
            <div className="question">
                <h3>{question}</h3>
                <span className={`arrow ${isOpen ? 'down' : ''}`}>&#9660;</span>
            </div>
            {isOpen && <div className="answer">{answer}</div>}
        </div>
        </div>
    );
};

